import { useEffect, useState } from "react";
import Card from "./card";

const List = () => {

    const [lists, setLists] = useState([]);

    useEffect(() => {
        const ListsFetch = async () => {
            const response = await fetch('/',{method : 'GET'});
            const json = await response.json();
            console.log(json);

            if(response.ok){
                setLists(json);
            }
            if(!response.ok){
                setLists({error: "NO document returned from server"})
            }
        }
        ListsFetch();
    },[])

    return (
        <div>
            <h1>List of Books</h1>
            {/* List component fetch books list from the database */}

            <div className="listRow">
                {lists && lists.map((list) => (<div className="listBanner">
                    <Card key={list._id} list={list} error={list.error}/>
                </div>))}
            </div>
        </div>
    )
}
export default List;
