import './App.css';
import List from "./components/List";
import Upload from "./components/Upload";
import { useState } from 'react';
function App() {  

  const [fileName, setFileName] = useState('')
  const [error, setError] = useState(null)

  const submitHandler = async (e) => {
    e.preventDefault();
    const response = await fetch('/download',{
      method : 'POST',
      body : fileName,
      headers : {
          'Content-Type' : 'application/json'
      }



  })
  }  
  return (
    <div className="App">
      <header className="App-header">

        <div>LOGO</div>
        <form method="GET" className="searchForm" onSubmit={submitHandler}>
            <input
              type="search"
              name="serchedFile"
              placeholder="Search books here"
              onChange={(e) => setFileName(e.target.value)}
              value={fileName}
              className="searchInput"
            />
            <button className="searchButton" type='submit'><b>Search</b></button>
            { error && window.promp(response.json) }
        </form>
        <div><i className="menuIcon"/></div>
      </header>
      <div>
        <Upload />
        <List />
      </div>
      <footer>
        <p>@Uxer Company limited</p>
      </footer>
    </div>
  );
}

export default App;
