import { useState } from 'react';
import { useHistory } from "react-router-dom";
const Header = () => {

    const [searchedFile, setSearchedFile] = useState('')

    const history = useHistory();
  
    // const submitHandler = async (e) => {
  
      // e.preventDefault();
  
      // console.log("Handle Submit function clicked");
  
      // const searchedBoxContent = { searchedFile: searchedFile }
  
      // const response = await fetch('/search', {
      //   method : 'POST',
      //   body : JSON.stringify(searchedBoxContent),
      //   headers : {
      //     'Content-Type' : 'application/json'
      //   }
      // })
  
  
      // const json = await response.json();
  
      // if (!response.ok) {
      //   console.log("Error");
      // }
      // if (response) {
      //     setSearchedFile('');
      // }
  
    // }
    
    const inputReset = (e) => {

      e.preventDefault();
      history.push('/ser')
      setSearchedFile('');
      console.log("Input Reset Clicked");

    }
    return (
        <header className="App-header">

        <div>LOGO</div>
        <form className="searchForm" onSubmit={inputReset} action='/search'>
            <input
              type="search"
              name="searchedFile"
              placeholder="Search books here"
              onChange={(e) => setSearchedFile(e.target.value)}
              value={ searchedFile }
              className="searchInput"
              required
            />

            <button className="searchButton" type='submit' onChange={inputReset}><b>Search</b></button>
          </form>
        <div><i className="menuIcon"/></div>
      </header>

    )
}
export default Header;
