import './App.css';
import List from "./components/List";
import Upload from "./components/Upload";
import { useState } from 'react';
function App() {  

  const [serchedFile, setSerchedFile] = useState('')
  const [error, setError] = useState(null)

  const submitHandler = async (e) => {
    e.preventDefault();

    const response = await fetch('/download', {
      method : 'POST',
      body 
    })

    const json = await response.json();

    if (!response.ok) {
        setError(json.error)
    }
    if (response) {
        setSerchedFile('')
        setError(null)
    }
    console.log("Handle Submit function clicked");

  }  
  return (
    <div className="App">
      <header className="App-header">

        <div>LOGO</div>
        <form method="POST" className="searchForm" onSubmit={submitHandler}>
            <input
              type="search"
              name="serchedFile"
              placeholder="Search books here"
              onChange={(e) => setSerchedFile(e.target.value)}
              value={ serchedFile }
              className="searchInput"
            />
            <button className="searchButton" type='submit'><b>Search</b></button>
            { error && <div>{error}</div> }
        </form>
        <div><i className="menuIcon"/></div>
      </header>
      <div>
        <Upload />
        <List />
      </div>
      <footer>
        <p>@Uxer Company limited</p>
      </footer>
    </div>
  );
}

export default App;
