    // const searchedData = { bookName: 'Chemistry' }
    const fetchSearch = async (searchedData) => {
      const response = await fetch('/a[pi/search', {
        method : 'POST',
        body : JSON.stringify(searchedData),
        headers : {
          'Content-Type' : 'application/json'
        }
      })
        
        const json = await response.json();
        
          if (response) {
            setResult(json);
          }
        }
        fetchSearch();
    },[]);
    return (
        <div className="search-div">
            {/* <Card  result={result} /> */}
        </div>
    )
}
