import './App.css';
import List from "./components/List";
import Upload from "./components/Upload";
import { useState } from 'react';
function App() {  

  const [searchedFile, setSearchedFile] = useState('')
  const [error, setError] = useState(null)

  const submitHandler = async (e) => {

    e.preventDefault();

    console.log("Handle Submit function clicked");

    const searchedBoxContent = { searchedFile: searchedFile }

    const response = await fetch('/download', {
      method : 'POST',
      body : JSON.stringify(searchedBoxContent),
      headers : {
        'Content-Type' : 'application/json'
      }
    })


    const json = await response.json();

    if (!response.ok) {
        setError(json.error)
    }
    if (response) {
        setSearchedFile('');
        setError(null);
    }

  }  
  return (
    <div className="App">
      <header className="App-header">

        <div>LOGO</div>
        <form method="POST" className="searchForm" onSubmit={submitHandler}>
            <input
              type="search"
              name="searchedFile"
              placeholder="Search books here"
              onChange={(e) => setSearchedFile(e.target.value)}
              value={ searchedFile }
              className="searchInput"
            />
            <button className="searchButton" type='submit'><b>Search</b></button>
            { error && <div>{error}</div> }
        </form>
        <div><i className="menuIcon"/></div>
      </header>
      <div>
        <Upload />
        <List />
      </div>
      <footer>
        <p>@Uxer Company limited</p>
      </footer>
    </div>
  );
}

export default App;
