import { useEffect, useState } from "react";
import Card from "./card";

const List = () => {

    const [lists, setLists] = useState([{"_id":"6493592230cde210340c90a6","bookName":"No way home","bookCategory":"Art","bookDescription":"The call","UploaderName":"Mahri","createdAt":"2023-06-21T20:10:10.384Z","updatedAt":"2023-06-21T20:10:10.384Z","__v":0},
                                        {"_id":"64924ee073adcf23488197e5","bookName":"Biology","bookCategory":"Novel/Novella","bookDescription":"sd ej","UploaderName":"Muhammad","createdAt":"2023-06-21T01:14:08.078Z","updatedAt":"2023-06-21T01:14:08.078Z","__v":0},
                                        {"_id":"6490647f1cefc20848583a47","bookName":"Physics","bookCategory":"Science","bookDescription":"The alchemy of souls","UploaderName":"Muhammad Al-ameen","createdAt":"2023-06-19T14:21:51.614Z","updatedAt":"2023-06-19T14:21:51.614Z","__v":0},{"_id":"6490644fdf02242230b53b58","bookName":"Chemistry","bookCategory":"Science","bookDescription":"The alchemy of souls","UploaderName":"Muhammad Hamza","createdAt":"2023-06-19T14:21:03.778Z","updatedAt":"2023-06-19T14:21:03.778Z","__v":0}]);

    useEffect(() => {
        const ListsFetch = async () => {
            const response = await fetch('/');
            const json = await response.json();
            console.log(json);

            if(response.ok){
                setLists(json)
            }
            if(!response.ok){
                console.log(json.error);
            }
        }
        ListsFetch();
    },[])

    return (
        <div>
            <h1>List of Books</h1>
            {/* List component fetch books list from the database */}

            <div className="listRow">
                {lists && lists.map((list) => (<div className="listBanner">
                    <Card key={list._id} list={list} />
                </div>))}
            </div>
        </div>
    )
}
export default List;
