import Card from "./card";

const List = () => {

    const [lists, setLists] = useState(null);

    useEffect(() => {
        const ListsFetch = async () => {
            const response = await fetch('/');
            const json = await response.json();
            console.log(json);

            if(response.ok){
                setLists(json)
            }
            
        }
        ListsFetch();
    },[])

    return (
        <div>
            <h1>List of Books</h1>
            {/* List component fetch books list from the database */}

            <div className="listRow">
                <div>Hello :{lists}</div>
                {/* {lists && lists.map((list) => (<div className="listBanner">
                    <Card key={list._id} list={list} />
                </div>))} */}
            </div>
        </div>
    )
}
export default List;
