const Card = (props) => {
    const json = props.json; 
    return (
        <div className="card-div">
            { json.map(() => (
                <div className="card-content"> 
            <div className="book-destription-div"></div>
                            <div className="book-detail-div">
                                <p><b>Name of book</b> : <small>{json.bookName}</small></p>
                                <p><b>Category</b> : <small>{json.bookCategory}</small></p>
                                <p><b>Author</b> : <small>{json.UploaderName}</small></p>
                            </div>
                        </div>
            ))}
        </div>
    )
}
export default Card;
