import { useState } from 'react';

const Header = ({ searchData, onSearchDataChange }) => {

    const [searchedFile, setSearchedFile] = useState('')
  
    const submitHandler = async (e) => {
      e.preventDefault();
  
      console.log('Handle Submit function clicked');
  
  
      const searchedBoxContent = { searchedFile: searchedFile };

      try {
        const response = await fetch('/api/search', {
          method: 'POST',
          body: JSON.stringify(searchedBoxContent),
          headers: {
            'Content-Type': 'application/json'
          }
        });
  
        if (!response.ok) {
          console.log('Error');
          return;
        }
  
        const data = await response.json();
        // Process the data as needed or update the searchData state
  
        // Clear the input after submitting the form
        setSearchedFile('');
      } catch (error) {
        console.error('Error:', error);
      }
      setSearchedFile('');
    };
    
    const inputReset = (e) => {
      e.preventDefault();
      setSearchedFile('');
      console.log("Input Reset Clicked");

    }
    return (
        <header className="App-header">

        <div>LOGO</div>
        <form className="searchForm" onSubmit={submitHandler} action='/search'>
            <input
              type="search"
              name="searchedFile"
              placeholder="Search books here"
              onChange={(e) => setSearchedFile(e.target.value)}
              value={ searchedFile }
              className="searchInput"
              required
            />

            <button className="searchButton" type='submit' onClick={inputReset}><b>Search</b></button>
          </form>
        <div><i className="menuIcon"/></div>
      </header>

    )
}
export default Header;
