import { useState } from 'react';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
const Header = () => {

    const [searchedFile, setSearchedFile] = useState('')
    const [error, setError] = useState(null)
  
    const submitHandler = async (e) => {
  
      e.preventDefault();
  
      console.log("Handle Submit function clicked");
  
      const searchedBoxContent = { searchedFile: searchedFile }
  
      const response = await fetch('/search', {
        method : 'POST',
        body : JSON.stringify(searchedBoxContent),
        headers : {
          'Content-Type' : 'application/json'
        }
      })
  
  
      const json = await response.json();
  
      if (!response.ok) {
          setError(json.error)
      }
      if (response) {
          setSearchedFile('');
          setError(null);
      }
  
    }
    return (
        <header className="App-header">

        <div>LOGO</div>
        <form className="searchForm" onSubmit={submitHandler} action='/search'>
            <input
              type="search"
              name="searchedFile"
              placeholder="Search books here"
              onChange={(e) => setSearchedFile(e.target.value)}
              value={ searchedFile }
              className="searchInput"
              required
            />
            <button className="searchButton" type='submit'><b>Search</b></button>
            { error && <div>{error}</div> }
        </form>
        <div><i className="menuIcon"/></div>
      </header>

    )
}
export default Header;
